import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './CurriculaPage.css';

function CurriculaPage() {
  const { fieldId } = useParams();
  const navigate = useNavigate();
  const [curricula, setCurricula] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [teacherPhotos, setTeacherPhotos] = useState({});

  useEffect(() => {
    fetch(`http://localhost:8080/api/curricula/${fieldId}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data.status === 'success') {
          setCurricula(data.data);
        } else {
          throw new Error('Data fetch failed');
        }
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, [fieldId]);

  const handleBack = () => {
    navigate('/');
  };

  const handlePhotoChange = (teacherId, event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setTeacherPhotos(prev => ({
          ...prev,
          [teacherId]: e.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="curricula-page">
      <button onClick={handleBack} className="back-button">Назад</button>
      <h1>Учебные планы</h1>
      {curricula && (
        <div className="curricula-details">
          <h2>{curricula.studyPlanName}</h2>
          <p>Курс: {curricula.course}</p>
          <p>Годы: {curricula.yearStart} - {curricula.yearEnd}</p>
          <p>Статус: {curricula.status}</p>
          <h3>Направление:</h3>
          <p>{curricula.fieldOfStudy.fieldCode} - {curricula.fieldOfStudy.fieldName}</p>
          <h3>Дисциплины:</h3>
          <div className="disciplines-list">
            {curricula.disciplines.map(discipline => (
              <div key={discipline.disciplineId} className="discipline-card">
                <h4>{discipline.disciplineName}</h4>
                <p>Семестр: {discipline.term}</p>
                <p>Общее часы: {discipline.totalHours}</p>
                <p>Отчет: {discipline.report}</p>
                <h5>Преподаватели:</h5>
                <ul>
                  {discipline.teachers.map(teacher => (
                    <li key={teacher.teacherId} className="teacher-item">
                      <div className="teacher-info">

                        <span>{teacher.fio} ({teacher.role})</span>
                      </div>

                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default CurriculaPage;
