import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './CurriculaPage.css';

function CurriculaPage() {
  const { fieldId } = useParams();
  const navigate = useNavigate();
  const [studyPlans, setStudyPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    setUserRole(role);

    fetch(`http://localhost:8080/api/admin/fields/${fieldId}/study-plans`, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data.status === 'success') {
          setStudyPlans(data.data);
        } else {
          throw new Error('Data fetch failed');
        }
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, [fieldId]);

  const handleBack = () => {
    navigate('/');
  };

  const handleDelete = async (planId) => {
    if (!window.confirm('Вы уверены, что хотите удалить этот учебный план?')) return;

    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`http://localhost:8080/api/admin/fields/${fieldId}/study-plans/${planId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        setStudyPlans(studyPlans.filter(plan => plan.studyPlanId !== planId));
      } else {
        alert('Ошибка при удалении учебного плана');
      }
    } catch (error) {
      console.error('Delete failed:', error);
      alert('Ошибка при удалении учебного плана');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="curricula-page">
      <button onClick={handleBack} className="back-button">Назад</button>
      <h2>Учебные планы для направления</h2>
      <div className="study-plans-list">
        {studyPlans.map(plan => (
          <div key={plan.studyPlanId} className="study-plan-card">
            <h3>{plan.studyPlanName}</h3>
            <p>Курс: {plan.course}</p>
            <p>Годы: {plan.yearStart} - {plan.yearEnd}</p>
            <p>Статус: {plan.status}</p>
            {(userRole === 'ADMIN' || userRole === 'MANAGER') && (
              <button onClick={() => handleDelete(plan.studyPlanId)} className="delete-button">
                Удалить
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default CurriculaPage;
