import React, { useState } from 'react';
import './Header.css';

function Header({ onSearch }) {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    if (onSearch) {
      onSearch(searchTerm);
    }
  };

  const handleHomeClick = () => {
    // Логика для возврата на домашнюю страницу, например, window.location.href = '/';
    console.log('Going home');
  };

  const handleAuthClick = () => {
    // Логика для авторизации
    console.log('Authenticating');
  };

  return (
    <header className="header">
      <div className="header-content">
        <button className="header-button home-button" onClick={handleHomeClick}>Home</button>
        <form onSubmit={handleSearchSubmit} className="search-form">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            className="search-input"
          />
          <button type="submit" className="header-button search-button">Search</button>
        </form>
        <button className="header-button auth-button" onClick={handleAuthClick}>Login</button>
      </div>
    </header>
  );
}

export default Header;
